// pageUrl / assetUrls ã®URLã‹ã‚‰ã‚¯ã‚¨ãƒªãƒ‘ãƒ©ãƒ¡ãƒ¼ã‚¿ã®æ–‡å­—åˆ— ã¨ ã‚¢ãƒ³ã‚«ãƒ¼ ã‚’å‰Šã‚Šãƒ™ãƒ¼ã‚¹URLã ã‘ã«ã™ã‚‹
// æ§‹é€ ã¨é †ç•ªã¯ç¶­æŒã€é‡è¤‡æ’é™¤ã—ãªã„

// URLæ­£è¦åŒ–é–¢æ•°ï¼ˆ? / # ä»¥é™ã‚’å‰Šã‚‹ï¼‰
function stripQueryAndAnchor(raw) {
  if (typeof raw !== 'string' || raw.length === 0) return raw;

  try {
    // æ§‹æ–‡è§£æã€‚æ­£è¦URLãªã‚‰ origin + pathname ã ã‘æ®‹ã™
    const parsedUrl = new URL(raw);
    return parsedUrl.origin + parsedUrl.pathname;
  } catch {
    // è§£æã§ããªã„ãƒ‘ã‚¿ãƒ¼ãƒ³ã¯ã€? ã¨ # ã‚’æ¢ã—ã¦ãã‚Œã‚ˆã‚Šã‚‚å‰ã ã‘ã‚’è¿”ã™
    const queryOrAnchorIndex = raw.search(/[?#]/);
    if (queryOrAnchorIndex === -1) return raw;
    return raw.slice(0, queryOrAnchorIndex);
  }
}

const out = [];

for (const item of items) {
  const jsonData  = item.json || {};

  //ãƒšãƒ¼ã‚¸URLå–å¾—
  const pageUrl = jsonData .pageUrl;
  //ã‚¢ã‚»ãƒƒãƒˆURLç¾¤å–å¾—
  const assetUrlList = Array.isArray(jsonData .assetUrls) ? jsonData .assetUrls : [];
  //URLæ­£è¦åŒ–
  const cleanedPageUrl = stripQueryAndAnchor(pageUrl);
  const cleanedAssetUrls = assetUrlList.map(stripQueryAndAnchor);

  out.push({
    json: {
      // ä»–ã«ãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰ãŒã‚ã‚Œã°ç¶­æŒã—ãŸã„å ´åˆã¯ã‚¹ãƒ—ãƒ¬ãƒƒãƒ‰
      // ...jsonData ,
      pageUrl: cleanedPageUrl,
