return out;

// URLã«ã¤ã„ã¦ã€Pageã¨ã‚¢ã‚»ãƒƒãƒˆã®åŒºåˆ†ã‚’ç„¡ãã—ã€å®Œå…¨ã«ãƒ¦ãƒ‹ãƒ¼ã‚¯ãªURLãƒªã‚¹ãƒˆã‚’ä½œæˆ

// Setã§é‡è¤‡æ’é™¤
const uniqueUrls = new Set();

// å„ã‚¢ã‚¤ãƒ†ãƒ ã‹ã‚‰ pageUrl ã¨ assetUrls ã‚’é›†ã‚ã‚‹
for (const item of items) {
  const jsonData = item.json || {};

  const pageUrlRaw = jsonData.pageUrl;
  const assetUrlList = Array.isArray(jsonData.assetUrls) ? jsonData.assetUrls : [];

  // ãƒšãƒ¼ã‚¸URLã‚’è¿½åŠ 
  if (typeof pageUrlRaw === 'string' && pageUrlRaw.length > 0) {
    uniqueUrls.add(pageUrlRaw);
  }

  // ã‚¢ã‚»ãƒƒãƒˆURLç¾¤ã‚’è¿½åŠ 
  for (const assetUrlRaw of assetUrlList) {
    if (typeof assetUrlRaw === 'string' && assetUrlRaw.length > 0) {
      uniqueUrls.add(assetUrlRaw);
    }
  }
}

// Set â†’ n8n items ã«å¤‰æ›ï¼ˆé‡è¤‡æ’é™¤æ¸ˆã¿ï¼‰
return Array.from(uniqueUrls).map((url) => ({
  json: { targetUrl: url },
}));

const out = [];

// URLæ–‡å­—åˆ—ã‚’ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—ã™ã‚‹é–¢æ•° (ã‚ªãƒªã‚¸ãƒŠãƒ«ã‚’å®Œå…¨ç¶­æŒ)
function cleanUrlString(inputUrl) {
  if (typeof inputUrl !== 'string') return inputUrl;

  // HTMLã‚¨ãƒ³ãƒ†ã‚£ãƒ†ã‚£ã¨ã‚¨ã‚¹ã‚±ãƒ¼ãƒ—ã®é™¤å»
  let normalizedUrl = inputUrl
    .replace(/&quot;/g, '\"')
    .replace(/\\\"/g, '\"')
    .replace(/\\'/g, "'");

  // å…ˆé ­ã¨æœ«å°¾ã®ä¸¡æ–¹ã‹ã‚‰ã‚¯ã‚©ãƒ¼ãƒˆã‚’é™¤å»
  normalizedUrl = normalizedUrl.replace(/^[\"']+|[\"']+$/g, '');

  return normalizedUrl.trim();
}

// -------------------------------------------------------------------
// ã€è¨­å®šç®‡æ‰€ 1ã€‘é™¤å¤–å¯¾è±¡ãƒ‘ã‚¿ãƒ¼ãƒ³ä¸€è¦§ (ã‚ªãƒªã‚¸ãƒŠãƒ«ã¨å®Œå…¨ä¸€è‡´)
// -------------------------------------------------------------------
const EXCLUDED_URL_PATTERNS = [
  '/wp-json/',
  '/admin-ajax.php',
  '/xmlrpc.php',
  '/feed',
  'contact',
  '/search',
  '/page',
  '/blog',
  'ã€‚',
  'beacon.min.js',
];

// -------------------------------------------------------------------
// ã€è¨­å®šç®‡æ‰€ 2ã€‘ å®Œå…¨ä¸€è‡´ã§é™¤å¤–ã™ã‚‹ CDN ãƒ«ãƒ¼ãƒˆURL (ã‚ªãƒªã‚¸ãƒŠãƒ«ã¨å®Œå…¨ä¸€è‡´)
// -------------------------------------------------------------------
const EXCLUDED_ROOT_URLS = [
  'https://cdn-ileamdm.nitrocdn.com/',
  'https://cdn-ileamdm.nitrocdn.com',
];

/**
 * UUIDã®æ­£è¦è¡¨ç¾ (8-4-4-4-12å½¢å¼)
 * ã€ä¿®æ­£æ¸ˆã€‘ã‚ªãƒªã‚¸ãƒŠãƒ«ã¨å®Œå…¨ã«ä¸€è‡´ã•ã›ã€åŠ£åŒ–ã‚’è§£æ¶ˆ
 */
const UUID_PATTERN = /[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}/i;

// URLãŒé™¤å¤–å¯¾è±¡ã‹åˆ¤å®š (ã‚ªãƒªã‚¸ãƒŠãƒ«ã‚’ç¶­æŒ)
function isExcludedUrl(targetUrl) {
  return EXCLUDED_URL_PATTERNS.some(pattern => targetUrl.includes(pattern));
}

// http/httpsä»¥å¤–ã¯ç„¡åŠ¹ (ã‚ªãƒªã‚¸ãƒŠãƒ«ã‚’ç¶­æŒ)
function isValidHttpUrl(targetUrl) {
  if (typeof targetUrl !== 'string') return false;
  return /^https?:\/\//i.test(targetUrl);
}

// -------------------------------------------------------------------
// ã€è¿½åŠ æ©Ÿèƒ½ã€‘ã‚¢ã‚»ãƒƒãƒˆãƒ•ã‚¡ã‚¤ãƒ«åˆ¤å®š (æ•‘æ¸ˆãƒ­ã‚¸ãƒƒã‚¯ç”¨)
// -------------------------------------------------------------------
function isAssetFile(url) {
  const assetExtensions = /\.(js|css|png|jpg|jpeg|gif|svg|webp|woff2?|ttf|otf|ico|bmp)$/i;
  return assetExtensions.test(url);
}

// ==========================================
// ãƒ¡ã‚¤ãƒ³å‡¦ç†ãƒ«ãƒ¼ãƒ— (æ—§ã‚³ãƒ¼ãƒ‰ã®å …ç‰¢æ€§ã‚’æœ€å„ªå…ˆ)
// ==========================================
for (const item of items) {
  const rawUrl = item.json?.targetUrl;
  if (typeof rawUrl !== 'string' || rawUrl.length === 0) continue;

  const cleanedUrl = cleanUrlString(rawUrl);

  // 1. å®Œå…¨ä¸€è‡´ã®ãƒ«ãƒ¼ãƒˆURLã‚’é™¤å¤– (ã‚ªãƒªã‚¸ãƒŠãƒ«é€šã‚Š)
  if (EXCLUDED_ROOT_URLS.includes(cleanedUrl)) continue;

  // 2. UUIDå½¢å¼ã®ãƒ‘ã‚¿ãƒ¼ãƒ³ã‚’é™¤å¤– (ã‚ªãƒªã‚¸ãƒŠãƒ«é€šã‚Š / 404å¯¾ç­–)
  // ã“ã“ã§392ä»¶ã®ã‚´ãƒŸã‚’ç¢ºå®Ÿã«æŠ¹æ®ºã—ã¾ã™
  if (UUID_PATTERN.test(cleanedUrl)) continue; 

  // 3. ã€è¿½åŠ ã€‘ã‚¢ã‚»ãƒƒãƒˆæ•‘æ¸ˆ (contactãŒå«ã¾ã‚Œã¦ã„ã¦ã‚‚ã€js/cssãªã‚‰ã“ã“ã§åˆæ ¼ã•ã›ã‚‹)
  if (isAssetFile(cleanedUrl)) {
    out.push({ json: { targetUrl: cleanedUrl } });
    continue;
  }

  // 4. ãƒ‘ã‚¿ãƒ¼ãƒ³ãƒãƒƒãƒãƒ³ã‚°ã«ã‚ˆã‚‹é™¤å¤– (ã‚ªãƒªã‚¸ãƒŠãƒ«é€šã‚Š / wp-json, feedç­‰)
  if (isExcludedUrl(cleanedUrl)) continue;

  // 5. http(s)ä»¥å¤–ã¯ã‚¹ã‚­ãƒƒãƒ— (ã‚ªãƒªã‚¸ãƒŠãƒ«é€šã‚Š)
  if (!isValidHttpUrl(cleanedUrl)) continue;

  out.push({ json: { targetUrl: cleanedUrl } });
}

return out;

// items[*].json.url ã‚’è¦‹ã¦ json.urltype ã‚’ä»˜ä¸
//
// â‘  ã‚¢ã‚»ãƒƒãƒˆæ‹¡å¼µå­ (.css/.js/.jpg/.jpeg/.gif/.png/.webp/.avif/å„ç¨®ãƒ•ã‚©ãƒ³ãƒˆ) ãŒ URL æœ«å°¾ã«ã‚ã‚‹ â†’ "asset"
// â‘¡ ã‚¢ã‚»ãƒƒãƒˆæ‹¡å¼µå­ãŒ URL æœ«å°¾ã«ç„¡ã„ã€ã‚‚ã—ãã¯ /(ã‚¹ãƒ©ãƒƒã‚·ãƒ¥) ã‚‚ã—ãã¯ .html / .htm â†’ "main_document"
// â‘¢ ä¸Šè¨˜ä»¥å¤– "exception"

const ASSET_EXTENSIONS = [
