  const inputUrl = item.json.targetUrl.trim();

  let urlPath = '/';

  // ãƒ›ã‚¹ãƒˆã‚ˆã‚Šå¾Œã‚ï¼ˆãƒ‘ã‚¹éƒ¨åˆ†ï¼‰ã‚’å–ã‚Šå‡ºã™
  try {
    const parsedUrl = new URL(inputUrl);
    urlPath = parsedUrl.pathname || '/';
  } catch (error) {
    // ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯: ãƒ—ãƒ­ãƒˆã‚³ãƒ«ï¼‹ãƒ›ã‚¹ãƒˆã‚’å‰Šã‚‹
    const match = inputUrl.match(/^[a-zA-Z][a-zA-Z0-9+.-]*:\/\/[^/]+(\/.*)?$/);
    urlPath = (match && match[1]) ? match[1] : '/';
  }


  // ãƒ‘ã‚¹ã®æœ€å¾Œã®è¦ç´ ï¼ˆ/ ã ã‘ãªã‚‰ç©ºæ–‡å­—ï¼‰
  const pathSegments = urlPath.split('/').filter(Boolean);
  const lastPathSegment = pathSegments.length ? pathSegments[pathSegments.length - 1] : '';

  // æ‹¡å¼µå­ã‚’å–å¾—ï¼ˆç„¡ã‘ã‚Œã°ç©ºæ–‡å­—ï¼‰
  let fileExtension = '';
  const dotIndex = lastPathSegment.lastIndexOf('.');
  if (dotIndex !== -1 && dotIndex < lastPathSegment.length - 1) {
    fileExtension = lastPathSegment.slice(dotIndex + 1).toLowerCase();
  }

  //URLã‚¿ã‚¤ãƒ—åˆ†é¡ã‚’é–‹å§‹

  let urlCategory;

  // â‘  ã‚¢ã‚»ãƒƒãƒˆæ‹¡å¼µå­ãŒ URL æœ«å°¾ã«ã‚ã‚‹
  if (ASSET_EXTENSIONS.includes(fileExtension)) {
