  } else if (
    fileExtension === '' ||             // ã‚¢ã‚»ãƒƒãƒˆæ‹¡å¼µå­ãªã—
    urlPath.endsWith('/') ||            // URLæœ«å°¾ãŒã‚¹ãƒ©ãƒƒã‚·ãƒ¥
    fileExtension === 'html' ||
    fileExtension === 'htm'
  ) {
