 */
function flattenNestedObject(targetObject, parentKey = '', flattenedResult = {}) {
  for (const [currentKey, currentValue] of Object.entries(targetObject)) {
    const combinedKey = parentKey ? `${parentKey}.${currentKey}` : currentKey;

    if (
      currentValue !== null &&
      typeof currentValue === 'object' &&
      !Array.isArray(currentValue)
    ) {
      // å†å¸°å‡¦ç†ã§ã•ã‚‰ã«æ·±ã„éšå±¤ã‚’å±•é–‹
      flattenNestedObject(currentValue, combinedKey, flattenedResult);
    } else {
      // å€¤ã‚’ã‚»ãƒƒãƒˆï¼ˆnull ã‚„ undefined ã¯ç©ºæ–‡å­—ã«å¤‰æ›ï¼‰
      flattenedResult[combinedKey] = currentValue === undefined ? '' : currentValue;
    }
  }
  return flattenedResult;
}

