const items = $input.all();

return items.map(item => {
  const j = item.json || {};
  const h = j.headers || {};

  // statusCode ã¨ statusMessage ã‚’ "200 OK" ã®ã‚ˆã†ãªå½¢ã«çµåˆ
  const statusCode = j.statusCode;
  const statusMessage = j.statusMessage;
  let combinedStatus = '';

  if (statusCode !== undefined && statusCode !== null) {
    combinedStatus = String(statusCode);
  }
  if (statusMessage) {
    combinedStatus = combinedStatus
      ? `${combinedStatus} ${statusMessage}`
      : String(statusMessage);
  }

  return {
    json: {
      // â–  headers.* â†’ headers.response-headers.*
      'headers.response-headers.server': h.server ?? '',
      'headers.response-headers.date': h.date ?? '',
      'headers.response-headers.content-type': h['content-type'] ?? '',
      'headers.response-headers.transfer-encoding': h['transfer-encoding'] ?? '',
      'headers.response-headers.connection': h.connection ?? '',
      'headers.response-headers.vary': h.vary ?? '',
      'headers.response-headers.link': h.link ?? '',

      // â–  statusCode + statusMessage â†’ headers.general.status-code
      'headers.general.status-code': combinedStatus,
    },
  };
});

return items.map(item => {
  // å…ƒã® headers ã‚’é€€é¿ã—ã¦ãŠã
  const originalHeaders = item.json.headers;

  // ãƒ¬ã‚¹ãƒãƒ³ã‚¹ body ãŒã‚ã‚Œã°ã€json ã®ãƒ«ãƒ¼ãƒˆã«å¼•ãä¸Šã’ (æ—¢å­˜å‡¦ç†)
  if (item.json.body) {
    item.json = item.json.body;
  }

  // é€€é¿ã—ã¦ãŠã„ãŸ headers ãŒã‚ã‚Œã°ã€ã‚­ãƒ¼åã« "headers." ã‚’ä»˜ã‘ã¦è¿½åŠ  (æ–°è¦å‡¦ç†)
  if (originalHeaders) {
    Object.keys(originalHeaders).forEach(key => {
      item.json[`headers.${key}`] = originalHeaders[key];
    });
  }

  return item;
});

